var nodemailer = require('nodemailer');

let smtpTransport = nodemailer.createTransport({
    host: 'smtp.gmail.com',
    port: 465,
    secure: true,
    auth: {
        type: 'OAuth2',
        user: 'trikhov@gmail.com',
        clientId: '702969627323-pcdhdi7vdsfbpke8gh5ru4r2hefotvmn.apps.googleusercontent.com',
        clientSecret: 'sEiAQvej3nBguNz7F5KDzn6N',
        refreshToken: '1/frPZ4IWpkGAa-BE_i-CX4TFPHOQEuIUFEjf2sp5ZGWE',
        accessToken: 'ya29.GlviBgdbOSvBqDESB1dhOTDdA0c7Z3_o61wK0Uf8wrLS_VikG3LrJrQvj8s1KuDl58RMm7ucLPwlhoYc6Lx2njMl6Jy2HgokJgNwf-YxOtkXZ3JwJjDWHx85SFmS'
    }
});
// Setup mail configuration
var mailOptions = {
    from: 'trikhov@gmail.com', // sender address
    to: "tryc_khov@yahoo.com", // list of receivers
    subject: 'Test Hello World', // Subject line
    text: 'Hey just testing to see if this thing works!', // plaintext body
  };

// send mail
export const email = mailOptions => {
  smtpTransport.sendMail(mailOptions, function(error, info) {
      if (error) {
        console.log(error);
        return res.notOk({
          status: 'error',
          msg: 'Email sending failed'
        })
      } else {
        console.log('Message %s sent: %s', info.messageId, info.response);
        return res.ok({
          status: 'ok',
          msg: 'Email sent'
        })
      }
      smtpTransport.close();
    });
}
