import React from 'react';
import navigation from '../css/navigation.css';

class Navigation extends React.Component {
  state = {
    english: [{tab: "Home"}, {tab: "About"}, {tab: "Service"}, {tab: "Apply"}],
    spanish: [{tab: "sHome"}, {tab: "sAbout"}, {tab: "sService"}, {tab: "sApply"}]
  }
  // tabsEnglish = [
  //   {tab: "Home"},
  //   {tab: "About"},
  //   {tab: "Service"}
  // ];
  //
  // tabsSpanish = [
  //   {tab: "eHome"},
  //   {tab: "eHome"},
  //   {tab: "eHome"}
  // ]

  // this function makes the tabs of the navigation
  makeTabs(language = "english") {
    if (language == "spanish") {
      return this.state.english.map(tabName => {
        return <div className="tab" key={tabName.tab}> {tabName.tab} </div>
      });
    } else {
      return this.state.spanish.map(tabName => {
        return <div className="tab" key={tabName.tab}> {tabName.tab} </div>
      });
    };
  };






  render() {
    return(
      <div className="nav-container">
        <div className="logo-container">
          Logo
        </div>
        <div className="tab-container">
          {this.makeTabs()}
          {/*// put the tabs here
          // each tab gets a different div*/}
        </div>
      </div>
    );
  }
}

export default Navigation;
