import React from 'react';

class Navigation extends React.Component {

  tabs = [
    {tab: "Home"},
    {tab: "About"},
    {tab: "Service"}
  ];

  // this function makes the tabs of the navigation
  makeTabs(language = "english") {
    if (language == "spanish") {
      return this.tabs.map(tabName => {
        return <div className="tab"> {tabName.tab} </div>
      });
    } else {
      return this.tabs.map(tabName => {
        return <div className="tab"> {tabName.tab} </div>
      });
    };
  };






  render() {
    return(
      <div className="nav-container">
        <div className="logo-container"> {this.makeTabs("spanish")}
        </div>
        <div className="tab-container">
          {/*// put the tabs here
          // each tab gets a different div*/}
        </div>
        Navigation works
      </div>
    );
  }
}

export default Navigation;
