import axios from 'axios';


const axiosMain = axios.create({
  baseURL: "https://sendgrid.com/v3"
});

export default axiosMain.get("/send", {
  header: {
    Authorization: 'Bearer' + process.env.SENDGRID_API_KEY
  }
});


