import React from "react";
import { Link } from "react-router-dom";
import Navigation from "./Navigation";
import style from "../css/servicepage.css";




class ServicePage extends React.Component {

  state = {
      name: '',
      email: '',
      phoneNumber: '',
      origin: '',
      destination: '',
      weight: '',
    };

  info = [
    { name: "name", header: "Name" },
    { name: "email", header: "Email" },
    { name: "phone_number", header: "Phone Number" },
    { name: "origin", header: "Origin" },
    { name: "destination", header: "Destination" },
    { name: "weight", header: "Weight" },

  ];

  labelQuestions = ["Your Name", "Email", "Phone Number", "Pick Up From", "Drop Off To", "Weight", "Dry Van", "Price"];

  // uses labelQuestions to put forms labels on left side
  label = formLabels => {
    return formLabels.map(question => {
      return(
        <div>
          <h3 className="label"> {question} </h3>
          <input className="input-box" type="text" name="name"/>
        </div>
      )
    });
  };


  generateQuestions = requestInfo => {
    return requestInfo.map(question => {
      if (question.header == "Email") {
        return (
          <div className="input-container" key={question.header}>
            <input
              className="input-box"
              type="text"
              name={question.name}
              pattern="^(.+)@(.+).com$"
              title="Please enter a valid email"
              onChange={e => this.setState({ email: e.target.value })}
            />
          </div>
        );
      }
      return (
        <div className="input-container" key={question.header}>
          <input className="input-box" type="text" name={question.name} />
        </div>
      );
    });
  };


  render() {
    return (
      <section style={{ backgroundColor: "#10316b", height: "100%" }}>
      <div id=" " style={{ height: "78px" }} />
      <form>
        {this.label(this.labelQuestions)}
      </form>

        {/*<form>
          <input className="input-box" type="text" name="name"/>
          <input className="input-box" type="text" name="email" />
          <input className="input-box" type="text" name="phone" />
          <input className="input-box" type="text" name="pick" />
          <input className="input-box" type="text" name="drop"/>
          <input className="input-box" type="text" name="weight" />
          <input className="input-box" type="text" name="price" />
          <input className="input-box" type="text" name="price" />
          <input type="submit" className="submit-button"/>
        </form>*/}
          {/* <form action="/send_email" method="post">
            {this.generateQuestions(this.info)}
            <div className="input-container">
              <h2> Dry Van? </h2>
              <input
                className="dry-van-yes"
                type="radio"
                name="dryVan"
                value="Yes"
                defaultChecked
              />
              Yes
              <input
                className="dry-van-no"
                type="radio"
                name="dryVan"
                value="No"
              />
              No
            </div>
            <div className="input-container">
              <h2> Price </h2>
              $<input className="input-box" type="text" name="Price" />
            </div>
            <input type="submit" className="submit-button"/>
          </form> */}
      </section>
    );
  }
}

export default ServicePage;
