import React from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Route, Link, Switch } from "react-router-dom";
import Navigation from "./Navigation";
import style from "../css/servicepage.css";

class ServicePage extends React.Component {
  state = {
    contactName: "",
    phoneNumber: "",
    startLocation: "",
    endLocation: "",
    weight: 0,
    dryVan: true
  };

  render() {
    return (
      <section style={{ backgroundColor: "red", height: "100%" }}>
        <Navigation />
        <div style={{ height: "78px" }} />
        <div className="survey">
          <div className="contactName input-container">
            <h3> Name </h3>
            <input
              className="input-box"
              type="text"
              value={this.state.contactName}
              onChange={e => this.setState({ contactName: e.target.value })}
            />
          </div>
          <div className="phoneNumber input-container">
            <h3> Phone Number </h3>
            <input
              className="input-box"
              type="text"
              value={this.state.phoneNumber}
              onChange={e => this.setState({ phoneNumber: e.target.value})}
              placeHolder="(123)-456-7890"
              maxLength="14"
            />
          </div>
          <div className="locations input-container">
            <div className="start-end-box">
              <h3> Start </h3>
              <input
                className="input-box"
                type="text"
                value={this.state.startLocation}
                onChange={e => this.setState({ startLocation: e.target.value })}

              />
            </div>
            <div className="start-end-box">
              <h3> Destination </h3>
              <input
                className="input-box"
                type="text"
                value={this.state.endLocation}
                onChange={e => this.setState({ endLocation: e.target.value })}
              />
            </div>
          </div>
          <div className="weight input-container">
            <h3> Weight </h3>
            <input
              className="input-box"
              type="text"
              value={this.state.weight}
              onChange={e => this.setState({ weight: e.target.value })}
            />
          </div>
          <div className="dry input-container">
            <h3> Dry Van </h3>
            <input type="radio" name="choice" value="yes"  /> Yes
            <input type="radio" name="choice" value="no" /> No
          </div>
        </div>
        <form action="https://formspree.io/tryc_khov@yahoo.com" method="POST">
          <input type="text" name="name"/>
          <input type="email" name="_replyto"/>
          <input type="submit" value="Send" />
        </form>
      </section>
    );
  }
}

export default ServicePage;
