import React from "react";
import { Link } from "react-router-dom";
import Navigation from "./Navigation";
import style from "../css/servicepage.css";




class ServicePage extends React.Component {

  state = {
      name: '',
      email: '',
      phoneNumber: '',
      origin: '',
      destination: '',
      weight: '',
    };

  labelQuestions = ["Your Name", "Email", "Phone Number", "Pick Up From", "Drop Off To", "Weight", "Dry Van", "Price"];

  // uses labelQuestions to put forms labels on left side
  label = formLabels => {
    return formLabels.map(question => {
      if(question == "Dry Van") {
      return(
        <tr key={question}>
          <td>
            <strong className="label"> {question} </strong>
          </td>
          <td>
            <select className="dry-van" name="van">
              <option value="Yes">Yes</option>
              <option value="No">No</option>
            </select>
          </td>
        </tr>
      )
      } else {
        return(
            <tr key={question}>
              <td>
                <strong className="label"> {question} </strong>
              </td>
              <td>
                <input className="input-box" type="text" name={{question}}/>
              </td>
            </tr>
        )
      }
    });
  };



  render() {
    return (
      <section style={{ backgroundColor: "#10316b", height: "100%" }}>
      <div id=" " style={{ height: "78px" }} />
      <form action="/send_email" method="post">
        <table>
        <p> Info about form </p>
          <tbody>
            {this.label(this.labelQuestions)}
            <tr>
              <td>
                <input type="submit"/>
              </td>
            </tr>
          </tbody>
        </table>
      </form>
      </section>
    );
  }
}

export default ServicePage;
