import React from "react";
import style from "../css/servicepage.css";
import ReviewInfo from './ReviewInfo';



class ServicePage extends React.Component {

  handleSubmit = trigger => {
    trigger.preventDefault();
    console.log(this.state);
  }

  state = {
    name: '',
    email: '',
    phone: '',
    pick: '',
    drop: '',
    weight: '',
    van: '',
    price: ''
  }

  // // uses labelQuestions to put forms labels on left side
  // label = formLabels => {
  //   return formLabels.map(question => {
  //     if(question.info == "Dry Van") {
  //     return(
  //       <tr key={question.info}>
  //         <td>
  //           <strong className="label"> {question.info} </strong>
  //         </td>
  //         <td>
  //           <select className="dry-van" name={question.name}>
  //             <option value="Yes">Yes</option>
  //             <option value="No">No</option>
  //           </select>
  //         </td>
  //       </tr>
  //     )
  //     } else {
  //       return(
  //           <tr key={question.info}>
  //             <td>
  //               <strong className="label"> {question.info} </strong>
  //             </td>
  //             <td>
  //               <input className="input-box" type="text" name={question.name}/>
  //             </td>
  //           </tr>
  //       )
  //     }
  //   });
  // };


  render() {
    return (
      <section style={{ backgroundColor: "#10316b", height: "100%" }}>
      <div id=" " style={{ height: "78px" }} />
      <form onSubmit={this.handleSubmit}>
        <table>
          <tbody>
            <tr><td><p>Info about form</p></td></tr>
            <tr key="Your Name">
              <td>
                <strong className="label"> Your Name </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="name" onChange={e => this.setState({name: e.target.value})}/>
              </td>
            </tr>
            <tr key="Email">
              <td>
                <strong className="label"> Email </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="email" onChange={e => this.setState({email: e.target.value})}/>
              </td>
            </tr>
            <tr key="Phone Number">
              <td>
                <strong className="label"> Phone Number </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="phone" onChange={e => this.setState({phone: e.target.value})}/>
              </td>
            </tr>
            <tr key="Pick Up From">
              <td>
                <strong className="label"> Pick Up From </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="pick" onChange={e => this.setState({pick: e.target.value})}/>
              </td>
            </tr>
            <tr key="Drop Off To">
              <td>
                <strong className="label"> Drop Off To </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="drop" onChange={e => this.setState({drop: e.target.value})}/>
              </td>
            </tr>
            <tr key="Weight">
              <td>
                <strong className="label"> Weight </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="weight" onChange={e => this.setState({weight: e.target.value})}/>
              </td>
            </tr>
            <tr key="Dry Van">
              <td>
                <strong className="label"> Dry Van </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="van" onChange={e => this.setState({van: e.target.value})}/>
              </td>
            </tr>
            <tr key="Price">
              <td>
                <strong className="label"> Price </strong>
              </td>
              <td>
                <input className="input-box" type="text" name="price" onChange={e => this.setState({price: e.target.value})}/>
              </td>
            </tr>
            <tr>
              <td>
                <input type="submit" value="Review Info"/>
              </td>
            </tr>
          </tbody>
        </table>
      </form>
      </section>
    );
  }
}

export default ServicePage;
